INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('64373ae5-e1a9-50d5-b4fa-d2bfa76e52f1', 'PL010-0001', 'Członkowie załogi mają zakaz wnoszenia na pokład statku powietrznego:', 'Broni palnej, broni gazowej i materiałów wybuchowych.', 'Broni palnej i materiałów wybuchowych.', 'Broni gazowej i materiałów wybuchowych.', 'Broni palnej i broni gazowej.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('083c1a52-be15-58ad-8da1-c5905e046291', 'PL010-0002', 'Do czego jest zobowiązany dowódca statku powietrznego?', 'Do zapewnienia bezpieczeństwa statkowi powietrznemu i wykonywania lotu zgodnie z przepisami.', 'Do wykonywania lotu zgodnie z przepisami.', 'Do ubezpieczenia statku powietrznego.', 'Do zapewnienia bezpieczeństwa statkowi powietrznemu.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('984711fe-46ca-51be-9d1b-8961c4903277', 'PL010-0003', 'Do lotniska dolatuje statek powietrzny bez łączności. Aby ostrzec pilota, że lotnisko nie jest bezpieczne i nie należy na nim lądować, informator AFIS nada sygnał świetlny:', 'Serię błysków czerwonych.', 'Serię błysków białych.', 'Ciągły sygnał czerwony.', 'Serią błysków zielonych.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('5feada24-ce05-5051-a51e-fdadd0a73fb2', 'PL010-0004', 'Dostępność przestrzeni powietrznej może być czasowo ograniczona ze względu na:', 'Obronność państwa i bezpieczeństwo publiczne.', 'Warunki pogodowe.', 'Bezpieczeństwo publiczne.', 'Obronność państwa.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('254ebb49-ca62-5926-8aec-f8217265ddfc', 'PL010-0005', 'Kiedy ustaje obowiązek pomocy, spoczywający na dowódcy statku powietrznego który spostrzegł inny statek powietrzny lub morski, który uległ wypadkowi lub znalazł się w niebezpieczeństwie?', 'Gdy dowódca wie, że ktoś inny udziela pomocy w podobnych lub lepszych warunkach, niż on sam mógłby udzielić.', 'Tego rodzaju obowiązek nigdy nie ustaje.', 'Gdy zgodę wyrazi Prezes ULC.', 'Gdy zgodzę wyrazi minister właściwy do spraw transportu.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('99cc0a10-145f-5393-b2ed-372b6f071076', 'PL010-0006', 'Kiedy ustaje obowiązek pomocy, spoczywający na dowódcy statku powietrznego który zauważył katastrofę lub klęskę żywiołową?', 'Gdy dowódca wie, że ktoś inny udziela pomocy w podobnych lub lepszych warunkach, niż on sam mógłby udzielić.', 'Tego rodzaju obowiązek nigdy nie ustaje.', 'Gdy zgodę wyrazi Prezes ULC.', 'Gdy zgodzę wyrazi minister właściwy do spraw transportu.', NULL);
