INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('e0b0c49e-fc52-5ecb-98ac-b2f9c1262ea5', 'PL010-0337', 'W przypadku negatywnego wyniku sprawdzenia wiadomości lub umiejętności lotniczych dokonywanych w celu przedłużenia uprawnień lotniczych:', 'Prezes ULC zawiesza ważność uprawnienia przez dokonanie odpowiedniego wpisu do licencji.', 'Prezes ULC zabiera licencję w której jest wpisane uprawnienie.', 'Prezes ULC dokonuje wykreślenie członka personelu lotniczego z rejestru członków personelu lotniczego.', 'Minister właściwy do spraw transportu zawiesza ważność uprawnienia przez dokonanie odpowiedniego wpisu do licencji.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('5061ec97-8de2-5323-a9eb-2d710b787abd', 'PL010-0338', 'W przypadku negatywnego wyniku sprawdzenia wiadomości lub umiejętności lotniczych dokonywanych w celu przedłużenia uprawnień lotniczych:', 'W protokole sprawdzenia wiadomości i umiejętności lotniczych wskazuje się zakres dodatkowego szkolenia niezbędnego do przywrócenia uprawnienia.', 'Prezes ULC zabiera licencję w której jest wpisane uprawnienie.', 'Prezes ULC dokonuje wykreślenie członka personelu lotniczego z rejestru członków personelu lotniczego.', 'Minister właściwy do spraw transportu zawiesza ważność uprawnienia przez dokonanie odpowiedniego wpisu do licencji.', NULL);
