INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('621237c9-51f9-50ae-add9-3c28439dc203', 'PL010-0383', 'Czym się charakteryzuje szkolenie praktyczne na ziemi i w locie?', 'Nauczaniem umiejętności praktycznych - dla pilotów wszystkich specjalności.', 'Nauczaniem wiedzy lotniczej - dla wszystkich specjalności członków personelu lotniczego.', 'Nauczaniem naziemnym kontrolerów ruchu lotniczego.', 'Odbyciem nadzorowanej praktyki lotniczej.', NULL);
