INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('2c7d4934-64df-5ab8-ad59-d2241ea300f1', 'PL010-0402', 'Granica ważności zezwolenia kontroli ruchu lotniczego to:', 'Punkt, do którego ważne jest zezwolenie organu służby kontroli ruchu lotniczego, udzielone dowódcy statku powietrznego określony przez podanie nazwy odpowiedniego punktu meldowania lub lotniska albo granicy przestrzeni powietrznej kontrolowanej.', 'Punkt, do którego ważne jest zezwolenie organu służby kontroli ruchu lotniczego, udzielone dowódcy statku powietrznego określony przez podanie nazwy i częstotliwości kolejnego organu ruchu lotniczego, który wyda dalsze zezwolenie.', 'Punkt, do którego ważne jest zezwolenie organu służby kontroli ruchu lotniczego powietrznego określony przez udzielone dowódcy statku podanie pośredniego poziomu lotu innego niż poziom lotu na trasę zawarty w bieżącym planie lotu dla danego statku powietrznego.', 'Określony czas, w którym statek powietrzny powinien rozpocząć lot, aby osiągnąć lotnisko docelowe w przewidywanym czasie.', NULL);
