INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('65b0ac55-ad02-5ae0-be4b-4aede30cfaf7', 'PL010-0574', 'Rada Ministrów w drodze rozporządzenia wprowadza zakazy i ograniczenia w ruchu lotniczym, niezbędne ze względu na:', 'Ważny interes polityki zagranicznej RP.', 'Gospodarkę rolną.', 'Poszukiwanie i ratownictwo.', 'Gospodarkę leśną.', NULL);
