INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('407dd918-8b86-59f3-8f97-560dabe0ab33', 'PL010-0577', 'Służba informacji powietrznej w przestrzeni klasy G:', 'Nie ma obowiązku zapewniania separacji między statkami powietrznymi.', 'Ma obowiązek zapewnić separacje między statkami powietrznymi wykonującymi loty VFR - spec i loty IFR.', 'Ma obowiązek zapewnić separacje między statkami powietrznymi wykonującymi loty IFR i loty VFR.', 'Ma obowiązek zapewnić separacje między statkami powietrznymi wykonującymi loty IFR i loty IFR.', NULL);
