INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('45248990-cedc-572d-8658-4e9c61d8154e', 'PL010-0596', 'Teren, nad którym jest wykonywany lot VFR, wznosi się do wysokości 300 m AMSL (elewacja terenu). Lot ten na wys. 1350 m AMSL może być wykonany z prędkością nie większą, niż:', '250 kt IAS.', '250 kt TAS.', '463 km/h TAS.', '250 km/h IAS.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('d8973cc0-16df-54e9-ac92-eab8a2fb60d7', 'PL010-0597', 'Teren, nad którym jest wykonywany lot VFR, wznosi się do wysokości 300 m AMSL (elewacja terenu). Lot ten na wys. 1350 m AMSL może być wykonany, jeżeli odległość pionowa od chmur jest:', 'Nie mniejsza niż 300 m.', 'Nie mniejsza niż 150 m.', 'Nie mniejsza niż 500 m.', 'Nieokreślona - z widocznością ziemi lub wody.', NULL);
