INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('1df85ad7-2fe2-52ac-88e4-5d834e19fedc', 'PL010-0599', 'W czasie lotów zabrania się dokonywania wszelkich zrzutów ze statku powietrznego z wyjątkiem zrzutów dokonywanych na potrzeby?', 'Doświadczalnych i szkoleniowych oraz sportu i obsługi imprez masowych.', 'Tylko doświadczalnych i szkoleniowych.', 'Tylko sportu i obsługi imprez masowych.', 'Ochrony granic.', NULL);
