INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('4843e577-7d59-528d-9efc-c9961993bcde', 'PL010-0602', 'W jakiej formie Rada Ministrów może wprowadzić zakazy i ograniczenia w ruchu lotniczym?', 'W formie rozporządzenia.', 'W formie zarządzenia.', 'W formie publikacji w Dzienniku Urzędowym ULC.', 'W formie decyzji administracyjnej.', NULL);
