INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('1f5b9ced-442c-5959-9044-3e490a61a6af', 'PL010-0611', 'W przestrzeni klasy G lecą naprzeciw siebie samolot i śmigłowiec. Aby uniknąć zderzenia dowódcy tych statków powietrznych powinni:', 'Skręcić w prawo.', 'Skręcić w lewo.', 'Samolot odejść w górę.', 'Śmigłowiec odejść w górę.', NULL);
