INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('d401af1e-f901-5991-9a43-b4072792d7e2', 'PL010-0613', 'W przestrzeni powietrznej nad wodami otwartymi Morza Bałtyckiego mogą być wyznaczane strefy:', 'Niebezpieczne.', 'Zakazane.', 'Ograniczone.', 'RCA.', NULL);
