INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('d4a52d4e-f96a-5e00-8ed4-81e7ff25007b', 'PL010-0616', 'W przypadku negatywnego wyniku sprawdzenia umiejętności lotniczych dokonywanych w celu przedłużenia uprawnień lotniczych:', 'Prezes ULC zawiesza ważność uprawnienia .', 'EASA zabiera licencję w której jest wpisane uprawnienie.', 'Prezes ULC dokonuje wykreślenie członka personelu lotniczego z rejestru członków personelu lotniczego.', 'Minister Transportu zawiesza ważność uprawnienia.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('1af02c8f-6e1e-52cc-b239-6165972595c4', 'PL010-0617', 'Kandydat, który nie zaliczy wszystkich sekcji kontroli umiejętności przed datą upływu ważności uprawnienia na klasę lub typ:', 'Nie może korzystać z tego uprawnienia do czasu zaliczenia kontroli umiejętności.', 'Powinien zwrócić licencję Prezesowi ULC .', 'Powinien zasięgnąć porady lotniczo-lekarskiej.', 'Powinien zwrócić licencję ministrowi właściwemu do spraw transportu .', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('115300fc-c624-5632-be37-b4d283114ffa', 'PL010-0624', 'W przypadku stwierdzenia utraty łączności radiowej (RADIOCOM FAILURE) pilot nastawia transponder w modzie „A” na kod:', '7600.', '7500.', '7700.', '7000.', NULL);
