INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('541be475-2fbe-5442-a640-4758740ac356', 'PL030-0008', 'Środek ciężkości jest wyrażony w:', 'w procentach średniej cięciwy aerodynamicznej (% MAC) lub odległości (mm, cm, inch) położenia S.C. względem punktu pomiarowego "DATUM"', 'w procentach średniej cięciwy aerodynamicznej ciężkości (% MAC).', 'w procentach cięciwy aerodynamicznej przy kadłubie (% MAC).', 'w procentach średniej cięciwy aerodynamicznej liczonej w średniej rozpiętości (% MAC).', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('7f55cfea-a67d-5eb9-8856-05f153ebbc4e', 'PL030-0009', 'Środek ciężkości można zdefiniować jako:', 'Punkt, w którym skoncentrowana jest masa statku powietrznego.', 'Punkt, do którego przyłożone są wszystkie siły działające na statek powietrzny.', 'Punkt, do którego przyłożone są siły nośna i ciężkości - działające na statek powietrzny.', 'Punkt, do którego przyłożone są siły oporu działające na statek powietrzny.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('9581a03e-95c2-5aa5-9c7d-a32679690ef0', 'PL030-0010', 'Ważenie statku powietrznego (SP): odczyt na wadze pod przednim podwoziem 155 kg, odczyt - suma na głównym 320 kg. Odległość przód SP - przednie podwozie 0.8m, przód SP - główne 2.4 m. Jaka jest odległość przód SP - środek ciężkości?', '1.88 m', '2.92', '3.2', '1.68', NULL);
