INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('f88a3d03-7ebd-5d76-b325-b83000faa5eb', 'PL030-0012', 'Wyrażenie (wzór) na moment (siły): Moment =', 'siła (ciężar) mnożone przez ramię działania siły.', 'siła (ciężar) dzielone przez ramię działania siły.', 'siła (ciężar) x odległość środka ciężkości od datum.', 'ramię działania siły dzielone przez siłę (ciężar)', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('b5bc0494-fe23-5263-ae98-63ddf0a5d254', 'PL030-0013', 'Jeśli środek ciężkości znajduje się blisko skrajnego przedniego położenia to:', 'Nastąpi zmniejszenie prędkości wznoszenia.', 'Nastąpi zwiększenie prędkości wznoszenia.', 'Zmniejszy się opór kształtu', 'Zmniejszy się opór indukowany.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('39b7bd1c-3147-564e-a37c-4584f92db69a', 'PL030-0014', 'Jeśli środek ciężkości znajduje się przed skrajnym przednim położeniem to szybowiec będzie:', 'miał większa prędkość przeciągnięcia.', 'miał mniejszą prędkość przeciągnięcia.', 'miał mniejszą wymaganą do lotu poziomego siłę nośną na płacie, co spowoduje zmniejszenie prędkości przeciągnięcia.', 'miał mniejszą wymaganą do lotu poziomego siłę nośną na płacie, co spowoduje zwiększenie prędkości przeciągnięcia.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('411ef118-64dc-53bc-8294-06f335ad5eb3', 'PL030-0015', 'Jeśli środek ciężkości szybowca przesunie się przed skrajne przednie położenie to (między innymi):', 'Ze względu na konieczność większego wychylenia steru wysokości - wzrasta wymagana siła nośna na płacie - zwiększa się opór indukowany, co negatywnie wpływa na osiągi.', 'Ze względu na konieczność mniejszego wychylenia steru wysokości - zmniejsza się siła nośna na usterzeniu, co wymaga zwiększenia siły nośnej na płacie co zmniejsza opór indukowany i ma negatywny wpływ na osiągi.', 'Ze względu na konieczność mniejszego wychylenia steru wysokości - spada wymagana siła nośna na płacie - zmniejsza się prędkość przeciągnięcia.', 'Ze względu na konieczność większego wychylenia steru wysokości - spada wymagana siła nośna na płacie - zwiększa się prędkość przeciągnięcia.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('43f52867-a22d-5ae3-a8fe-8c3a06e30f50', 'PL030-0016', 'Masa pustego szybowca wynosi 275 kg, a maksymalna masa do startu wpisana jako ograniczenie w Instrukcji Użytkowania w Locie 395kg. Ile może ważyć pilot?', '120 kg ze spadochronem i całym wyposażeniem (bagażem), istnieje również ograniczenie masy minimalną (na ogół 55kg).', '120 kg bez spadochronu i wyposażenia.', 'Między 40 a 125 kg.', 'Między 55 kg a 125 kg.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('3f135692-2f54-5017-9ead-e88eff9254f6', 'PL030-0018', 'Szybowiec ma MAC 1.5 m, krawędź natarcia MAC znajduje się 1.9 m za datum szybowca, odległość datum-CG wynosi 2.4 m. Podaj położenie CG w % MAC:', '33', '25', '20', '0.5', NULL);
