INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('c4e8bb5e-210f-5251-928f-9ec04e392f68', 'PL030-0020', 'Szybowiec musi być wyważony ze względu na zachowanie stateczności:.', 'podłużnej.', 'poprzecznej.', 'statycznej poprzecznej.', 'dynamicznej poprzecznej i podłużnej.', NULL);
