INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('593822e0-02c3-5e4c-adcd-72832be96d7a', 'PL030-0038', 'Czy wysoka wilgotność przyczynia się do spadku osiągów samolotu:.', 'Tak.', 'Fałsz - chłodzi silnik i przez to poprawia osiągi.', 'Wilgotność powietrza nie ma wpływu na osiągi.', 'Wszystkie podane odpowiedzi są nieprawdziwe.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('b4722493-ac22-51cf-9ccd-1d8962e0e11f', 'PL030-0041', 'Dla każdej kombinacji wysokości lotu i masy samolotu, w zakresie prędkości minimalnej do maksymalnej istnieje:.', 'Taki kąt natarcia, przy którym siła nośna równa się ciężarowi i możliwy jest lot poziomy.', 'Takie przechylenie, które zapewnia, że siła nośna równa się ciężarowi i możliwy jest lot poziomy', 'Taki kąt natarcia, przy którym siła oporu równa się ciągowi wytwarzanemu przez jednostki napędowe i możliwy jest lot poziomy.', 'Taki kąt natarcia, przy którym siła nośna równa się ciężarowi razy cosinus kąta natarcia i możliwy jest lot poziomy.', NULL);
