INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('c51ed01a-774f-5296-9d1e-0d73002b7c0c', 'PL030-0049', 'Jakie będą efekty zmiany temperatury otoczenia na osiągi samolotu jeśli wszystkie inne parametry pozostaną niezmnienione:.', 'Spadek temperatury otoczenia spowoduje wzrost gradientu wznoszenia.', 'Wzrost temperatury otoczenia spowoduje spadek wymaganego dystansu do startu.', 'Spadek temperatury otoczenia spowoduje wzrost wymaganego dystansu do startu.', 'Wzrost temperatury otoczenia spowoduje spadek wymaganego dystansu do startu i lądowania.', NULL);
