INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('3c417b81-50e9-543a-ad99-f89e78466ed3', 'PL030-0072', 'Mała wysokość gęstościowa oznacza, że:.', 'Gęstość powietrza jest duża - czyli osiągi samolotu będą lepsze niż w przypadku dużej wysokości gestościowej.', 'Gęstość powietrza jest duża - czyli osiągi samolotu będą dużo gorsze niż w przypadku dużej wysokości gestościowej.', 'Gęstość powietrza jest mała, co powoduje spadek osiągów samolotu w porównaniu do przypadku dużej wysokości gestościowej.', 'Gęstość powietrza jest duża, co powoduje spadek osiągów samolotu w porównaniu do przypadku dużej wysokości gestościowej.', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('787a5fe0-9f9b-5c60-947f-7b9265fdd570', 'PL030-0078', 'Najbardziej ekonomiczny z punktu widzenia zużycia paliwa / zasięgu kąt natarcia to taki, który wytwarza:.', 'Najlepszy stosunek sił nośnej do oporu - taki najbardziej ekonomiczny kąt natarcia jest stały dla każdej kombinacji wysokości lotu i masy samolotu.', 'Najlepszy stosunek sił nośnej do oporu - taki najbardziej ekonomiczny kąt natarcia rośnie wraz z masą samolotu oraz wysokością lotu.', 'Najlepszy stosunek siły oporu do ciągu.', 'Najlepszy stosunek siły nośnej do ciągu.', NULL);
