INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('0ed66dc5-87a0-5e09-baa7-8432e907a11e', 'PL100-0103', 'W przypadku, gdy kolizja z linią wysokiego napięcia jest nieunikniona, należy:', 'Zamknąć zawory butli, opróżnić przewody paliwowe. Obniżyć wysokość lotu tak, aby zetknięcie z przewodami nastąpiło powłoką, a nie koszem', 'Włączyć palnik na pracę ciągłą i wyskoczyć z kosza przed zetknięciem z przewodami', 'Zmienić wysokość lotu tak, aby zetknięcie z przewodami nastąpiło koszem, a nie powłoką', 'Odepchnąć balon rękami od przewodów', NULL);
