INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('77cb87bd-1c90-55b2-9aa4-76baad59a3a8', 'PL100-0105', 'Postępowanie pilota w przypadku awarii płomieni pilotowych', 'Zamknąć dopływ paliwa zaworem butli, otworzyć jeden z zaworów palnika (główny albo pilotowy), częściowo otworzyć zawór butli, zapalić gaz wydobywający się z dyszy (dysz). Regulować wysokość lotu częściowo zamykając i otwierając zawór butli', 'Nie podejmować innych czynności poza przygotowaniem do twardego lądowania z dużą prędkością poziomą', 'Zamknąć wszystkie zawory palnika i butli, wysokość lotu balonu kontrolować poprzez wyrzucanie z kosza zbędnego wyposażenia', 'Wykorzystać płomień cichego palnika z częściowo tylko otwartym zaworem jako płomień pilotowy i kontynuować zaplanowany lot', NULL);
