INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('5e811702-ee91-5453-bc00-db5ae8a56918', 'PL100-0107', 'Postępowanie pilota w przypadku uszkodzenia powłoki w czasie lotu', 'Częściej używaj palnika, aby uzupełnić utraconą siłę nośną i utrzymać stałą prędkość opadania, jeżeli to konieczne wyrzuć za burtę balast w postaci zbędnego wyposażenia', 'Otworzyć klapę spadochronową, aby zmniejszyć wypływ gorącego powietrza w uszkodzonym miejscu', 'Do utrzymania lotu poziomego używaj cichego palnika, gdyż tylko on jest on wystarczająco wydajny w takiej sytuacji', 'Nie podejmuje się żadnej szczególnej procedury w takiej sytuacji', NULL);
