INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('77db9043-5793-5aab-8ae1-2e73eda851c6', 'PL100-0111', 'Lot balonem w śladzie aerodynamicznym samolotu:', 'Może powodować zakłócenia w locie balonu, a nawet jego uszkodzenie', 'Nie może powodować żadnych zakłóceń lotu balonu', 'Może spowodować błędy wskazań przyrządów pokładowych', 'Może spowodować jedynie chorobę lokomocyjną załogi balonu', NULL);
