INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('5a960fd7-c07f-551e-8098-963f0b1aa808', 'PL100-0116', 'Gwałtowne odciążenie balonu w czasie lotu (np. przy zrzucie skoczków lub lotni) prowadzące do przekroczenia maksymalnej prędkości wznoszenia grozi:', 'Wgnieceniem klapy spadochronowej do powłoki, a w skrajnych przypadkach zerwaniem linek centralizujących klapę', 'Niekontrolowanym wzrostem prędkości postępowej balonu i zmianą kierunku lotu', 'Nie grozi żadnymi konsekwencjami poza zwiększeniem wysokości lotu', 'Spadkiem ciśnienia gazu w instalacji paliwowej', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('da68fbb9-27e3-57fc-b64f-b2e352c7ee3b', 'PL100-0117', 'Procedury kontroli przed startem balonu obejmują sprawdzenie:', 'Powłoki - w tym kontrola klapy spadochronowej i dołączenie liny klapy, zespołu kosza, palnika - w tym płomieni pilotowych, paliwa, wyposażenia - w tym mapy, dodatkowe źródło ognia, przyrządy pokładowe, załadowanie balonu (temperatura w powłoce)', 'Działania klapy spadochronowej i palnika', 'Nie stosuje się żadnej procedury kontroli przed startem balonu', 'Działania klapy spadochronowej i palnika oraz sprawdzenia szczelności połączeń instalacji paliwowej za pomocą specjalnej piany lub wody z mydlinami', NULL);
INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('168b687f-e973-5700-8573-f1b7f48fda64', 'PL100-0118', 'Podejście do lądowania powinno odbywać się', 'w terenie wolnym od przeszkód, szczególnie w linii lotu', 'W kierunku na przeszkodę (np. linię elektryczną), ułatwi ona zatrzymanie balonu', 'Nie stosuje się żadnych reguł w doborze miejsc do lądowania', 'Po nawietrznej stronie jezior, lądowanie w wodzie najlepiej amortyzuje wstrząs przy lądowaniu', NULL);
