INSERT INTO "nauka-ppla_question" (id, "externalId", question, "answerCorrect", "answerIncorrect1", "answerIncorrect2", "answerIncorrect3", "explanationId") VALUES ('d1b38cad-6be2-5a2e-880e-f45c3d82bf8f', 'PL100-0123', 'Jeśli do zasilania płomieni pilotowych używany jest gaz w postaci lotnej (gazowej), to po położeniu kosza (przed postawieniem balonu) reduktory ciśnienia na butlach paliwowych muszą znajdować się z pozycji:', 'Górnej - tak, aby znajdowały się powyżej poziomu ciekłego gazu w butli', 'Dolnej - tak, aby znajdowały się poniżej poziomu ciekłego gazu w butli', 'Bocznej lub dolnej - ale tak, aby znajdowały się poniżej poziomu ciekłego gazu w butli', 'Położenie reduktorów ciśnienia na butlach względem poziomu gazu nie ma znaczenia', NULL);
